<?php

namespace Tests\Attributes\GitHubMarkdown;

require __DIR__ . '../../../../vendor/autoload.php';

use DBlackborough\Quill\Options;
use DBlackborough\Quill\Render as QuillRender;

/**
 * Image tests
 */
final class ImageTest extends \PHPUnit\Framework\TestCase
{
    private $delta_image = '{"ops":[{"insert": {"image": "data:image/png;base64,"}}]}';

    private $expected_image = '![Image](data:image/png;base64,)';

    /**
     * Image
     *
     * @return void
     * @throws \Exception
     */
    public function testImage()
    {
        $result = null;

        try {
            $quill = new QuillRender($this->delta_image, OPTIONS::FORMAT_GITHUB_MARKDOWN);
            $result = $quill->render();
        } catch (\Exception $e) {
            $this->fail(__METHOD__ . 'failure, ' . $e->getMessage());
        }

        $this->assertEquals(
            $this->expected_image,
            $result,
            __METHOD__ . ' Image failure'
        );
    }
}
